mapper.select:
	SELECT *FROM `finance_product` t
	WHERE t.`status`=1 

   	 AND t.`name` LIKE CONCAT('%',#{name},'%')


	 AND t.`type` IN

	#{item}



	 AND t.`apply_area_code` IN

	#{item}



	 AND t.`guarantee_type_code` IN

	#{item}



	 AND t.`organization_type` IN

	#{item}



	 AND t.`parent_organization_id` IN

	#{item}



	 AND 

	(${item})




	 AND 

	(${item})


	order by 

	${item.field} ${item.sort}
